def days_cur_month():
    m = datetime.datetime.now().month
    y = datetime.datetime.now().year
    if m == 12:
        m = 1
    ndays = (date(y, m+1, 1) - date(y, m, 1)).days
    d1 = date(y, m, 1)
    d2 = date(y, m, ndays)
    delta = d2 - d1

    return [(d1 + timedelta(days=i)).strftime('%Y-%m-%d') for i in range(delta.days + 1)]


