def calculate(born):
    today = date.today()
    extra_year = 0
    if born.month and born.day:
        extra_year = 1 if ((today.month, today.day) < (born.month, born.day)) else 0
    yr = 0
    if born.year:
        yr = (today.year - born.year) - extra_year
    return yr

