def get_in_val(self, time):
    if time:
        mins = "00"
        split = str(time).split(':')
        split.pop()
        for idx, val in enumerate(split):
            if idx == 0:
                hr = val 
            elif idx == 1:
                mins = val
        in_time = str(hr)+":"+str(mins)
        return str(in_time)
    else:
        return None 

